/* Q1 Find the percent of households from the Pacific division (division = 10) that made changes as a result of a free home energy audit
 among all Pacific division households that made changes as a result of any home energy audit. */
SELECT (COUNT (freeaudit):decimal / COUNT (allaudit))*100 as PercentFreeChange
FROM
      (SELECT COUNT (auditchg)
       FROM RECS_2015
       WHERE
       auditchg = 1 AND
       freeaudit = 1 AND
       division = 10) AS freeaudit,
      (SELECT count (auditchg)
       FROM RECS_2015
       WHERE
       auditchg = 1 AND
       division = 10) AS allaudit;
-- Q1 Answer: 20.49%

/* Q2 For each housing type, find the number of households that received an energy audit. Order by highest number of households type. */
SELECT typehuq,
             COUNT(typehuq) AS typehuq_hum
    FROM     RECS_2015
    WHERE    audit = 1
    GROUP BY typehuq
    ORDER BY typehuq_num DESC;
